%Perimetry Test% April 4,2004% Claudioclear all;close all;clear mex;[name,group,session,filename] = perimetrydlg;global display%Display Parameters%Change here any display parameter if neededdisplay = loadDisplayParams('screenNumber',0);display.distance=20;    %20 cm from the observer%display = usePredefinedDisplay('screenNumber', 1, 'resolution', '1024x768, 75Hz', 'cmapDepth', 10);display = openScreen(display);[stimParams, testParams] = setPerimetryParams(display,name,group,session,filename);welcomestring=['Welcome, press ' testParams.keyyes ' if you see stimulus, ' testParams.keyno ' if you do not see stimulus , ' testParams.keystop ' to quit'];pressKey2Begin(display,welcomestring);datetoday =date;timestart =clock;timestartt=[num2str(timestart(4)),':',num2str(timestart(5)),':',num2str(round(timestart(6)))];% Main routine[results,testParams]=findthresh(display, testParams, stimParams);timestop =clock;timesstopt=[num2str(timestop(4)),':',num2str(timestop(5)),':',num2str(round(timestop(6)))];%map=buildmap2(display,stimParams,results);%showmap(map);eval(['save ' filename]);screen('CloseAll');clear all;close all;