function map=buildpermap(display,stimParams,results)% map=buildpermap(display,stimParams,results) % To show results from perimetry test% after this command do showmap(map) to plot it% April 2004% Claudiocontrast=results.contrast;position=results.positions;%Initializing Mapmap = 0*ones(display.numPixels(2),display.numPixels(1));%Plotting result for n=1:size(position,1)	x0=ceil(position(n,2)-size(stimParams.image,2)/2);y0=ceil(position(n,1)-size(stimParams.image,1)/2);						for (j=1:size(stimParams.image,2))						for (k=1:size(stimParams.image,1))						    if (y0+k<=size(map,1) & x0+j<=size(map,2) & y0+k>=1 & x0+j>1)								map(y0+k,x0+j)=contrast(n);							end													end		      		end			 end	