function [status]=drawctst(image,location,contrast)%%    [status]=draw(image,location,contrast)%% Author: Claudio% Purpose:%   location [x1,y1]%% Example:%    To put the image at row 200, col, 350 with contrast 0.5%    drawImage(img,[200,350],0.5);%% See also:  cmapContrast to create a% NOT PART OF FUNCTIONdisplay = loadDisplayParams;display = openScreen(display);%display.windowPtr =screen(display.screenNumber,'OpenWindow');[cmap,gamma]=createcmap(display,contrast);SCREEN(display.windowPtr,'Gamma',gamma,display.pixelDepth)SCREEN(display.windowPtr,'SetClut',cmap);screen(display.windowPtr,'FillRect',127);location=[round(location(1)-size(image,1)/2),round(location(2)-size(image,2)/2),round(location(1)+size(image,1)/2),round(location(2)+size(image,2)/2)];screen(display.windowPtr,'PutImage',image,location);getchar;[cmap,gamma]=createcmap(display,0.8);SCREEN(display.windowPtr,'Gamma',gamma,display.pixelDepth)SCREEN(display.windowPtr,'SetClut',cmap);getchar;[cmap,gamma]=createcmap(display,0.2);SCREEN(display.windowPtr,'Gamma',gamma,display.pixelDepth)SCREEN(display.windowPtr,'SetClut',cmap);getchar;SCREEN(display.windowPtr,'Close');