function [status]=draw(image,location)%location [x1,y1,x2,y2]% NOT PART OF FUNCTIONdisplay = loadDisplayParams;display = openScreen(display);%display.windowPtr =screen(display.screenNumber,'OpenWindow');%screen(display,'FillRect',127);screen(display.windowPtr,'PutImage',image,location);% pausegetchar;[cmap,gamma]=createcmap(display,1);SCREEN(display.windowPtr,'Gamma',gamma,display.pixelDepth)SCREEN(display.windowPtr,'SetClut',cmap);getchar;[cmap]=createcmap(display,0.8);SCREEN(display.windowPtr,'Gamma',gamma,display.pixelDepth)SCREEN(display.windowPtr,'SetClut',cmap);getchar;[cmap]=createcmap(display,0.2);SCREEN(display.windowPtr,'Gamma',gamma,display.pixelDepth)SCREEN(display.windowPtr,'SetClut',cmap);getchar;SCREEN(display.windowPtr,'Close');