function finalimg=joindispimg(image,position,display)minVal = max([display.reservedColor(:).fbVal])+1;maxVal = display.numColors-1;backColorIndex = (maxVal-minVal)/2+1+minVal;finalimg = ones(display.numPixels(2),display.numPixels(1))*backColorIndex;x1= round(position(2)-size(image, 2)/2);  y1 = round(position(1)-size(image, 1)/2);% Add image for x=x1:x1+size(image, 2)-1	for y=y1:y1+size(image, 1)-1	finalimg(y,x)=image(y-y1+1,x-x1+1);		endend% Always select image that fits monitorfinalimg=finalimg(1:display.numPixels(2),1:display.numPixels(1));