function listfix=perilocations(display,fix_1,fix_2)%x_fix,y_fix are positions of fixation point% Satoshi : 30+30 = 60 degrees% in pixel unitsL=round(2*display.distance*tan(60*pi/(180*2))/display.pixelSize);% one row of pointslistfix(1,:) = [fix_1+round(L/10), fix_2+round(L/10)];listfix(2,:) = listfix(1,:)+[0,round(L/5)];listfix(3,:) = listfix(1,:)+[0,round(2*L/5)];listfix(4,:) = listfix(1,:)-[0,round(L/5)];listfix(5,:) = listfix(1,:)-[0,round(2*L/5)];listfix(6,:) = listfix(1,:)-[0,round(3*L/5)];for n=1:6listfix(6+n,:) = listfix(n,:)+[round(L/5),0];listfix(12+n,:) = listfix(n,:)-[round(L/5),0];listfix(18+n,:) = listfix(n,:)-[round(2*L/5),0];endlistfix(25,:) = listfix(1,:)+[round(2*L/5),0];listfix(26,:) = listfix(25,:)+[0,round(L/5)];listfix(27,:) = listfix(25,:)-[0,round(L/5)];listfix(28,:) = listfix(25,:)-[0,round(2*L/5)];listfix(29,:) = listfix(1,:)-[round(3*L/5),0];listfix(30,:) = listfix(29,:)+[0,round(L/5)];listfix(31,:) = listfix(29,:)-[0,round(L/5)];listfix(32,:) = listfix(29,:)-[0,round(2*L/5)];%plot(listfix(1:size(listfix,1),2),listfix(1:size(listfix,1),1),'+r')%hold%Current plot held%plot([1,display.numPixels(1),1,display.numPixels(1)],[1,1,display.numPixels(2),display.numPixels(2)],'*b')