function h = browseImages(imgs);%  h = browseImages(imgs);%% Simple GUI to browse through a cell array of images in imgs.%% ras, 05/2007.h = figure('Color', 'w', 'UserData', imgs, 'Name', mfilename, 'Colormap', gray(256));nIm = length(imgs(:));hAxes = axes('Units', 'norm', 'Position', [.1 .2 .8 .7]);imagesc(imgs{1}); axis image; colorbar% slider for paging through imgescb = ['tmp = get(gcf, ''UserData'');  ' ...  	  'n = round(get(gcbo, ''Value''));  ' ...	  'imagesc(tmp{n}); axis image; colorbar; ' ...	  'title( sprintf(''Image %i'', n) ); ' ...	  'clear tmp n '];hSlider = uicontrol('Style', 'slider', 'Units', 'norm', 'Position', [.1 .1 .8 .05], ...				'SliderStep', [1/(nIm+1) 3/(nIm+1)], 'BackgroundColor', 'w', ...				'Callback', cb, 'Min', 1, 'Max', nIm, 'Value', 1);				% add a colormap popupcb = ['str = get(gcbo, ''String'');  ' ...  	  'n = round(get(gcbo, ''Value''));  ' ...	  'colormap(str{n}); ' ...	  'clear tmp n '];str = {'gray'  'jet' 'hot' 'cool'};hPopup = uicontrol('Style', 'popup', 'Units', 'norm', 'Position', [.1 .01 .4 .04], ...				'String', str, 'Value', 1, 'BackgroundColor', 'w', ...				'Callback', cb);% add a grid on/off togglehPopup = uicontrol('Style', 'checkbox', 'Units', 'norm', 'Position', [.6 .01 .15 .04], ...				'String', 'Grid', 'Value', 0, 'BackgroundColor', 'w', ...				'Callback', 'grid');return				