function params = displayParams% Critical parametersparams.numPixels    = [1920 1080];params.dimensions   = [69.8 39.3]; % cmparams.distance     = 210;params.frameRate    = 120;params.cmapDepth    = 12;params.screenNumber = 0;% Descriptive parametersparams.position = 'CBI_7T_BOLDScreen';params.stereoFlag = 0;% parameters which can make programming the display easierparams.flipLR = 0;params.flipUD = 0;