function params = displayParams% For Scanner NEC LCD2080UX display% PR650 about 30 cm away, center at screen.% Critical parametersparams.numPixels = [800 600];params.dimensions = [40.800 30.600];params.distance = 280; params.frameRate = 60;params.cmapDepth = 8;params.screenNumber = 1;% Descriptive parametersparams.position = '3T Back Bore';params.stereoFlag = 0;% parameters which can make programming the display easierparams.flipLR = 1;params.flipUD = 0;