function params = displayParams% Critical parametersparams.numPixels    = [1024 768];params.dimensions   = [32 24]; % cmparams.distance     = 58;params.frameRate    = 60;params.cmapDepth    = 8;params.screenNumber = 1;% Descriptive parametersparams.position = 'CBI projector';params.stereoFlag = 0;% parameters which can make programming the display easierparams.flipLR = 0;params.flipUD = 0;