function params = displayParams% Calibrated by Kendrick and Franco on 2011/07/18% Critical parametersparams.numPixels    = [1280 800];params.dimensions   = [64.3 40.2]; % cmparams.distance     = 240; params.frameRate    = 60;params.cmapDepth    = 8;params.screenNumber = 1;% Descriptive parametersparams.position = 'CNI Back Bore';params.stereoFlag = 0;% parameters which can make programming the display easierparams.flipLR = 0;  % CNI display was initally L-R flipped, but as of 5.26.11 seems to no longer require flipparams.flipUD = 0;