function params = displayParams% Critical parametersparams.numPixels    = [1280 800];params.dimensions   = [29 14.2]; % cmparams.distance     = 210;params.frameRate    = 60;params.cmapDepth    = 8;params.screenNumber = 0;% Descriptive parametersparams.position = 'max macbook';params.stereoFlag = 0;% parameters which can make programming the display easierparams.flipLR = 0;params.flipUD = 0;